kmpç®—æ³• //kmp without optimization//strncpy() memmove()#include&lt;bits/stdc++.h&gt;using namespace std;int kmp(char s[],char p[]){//preprocessint sl=strlen(s);int pl=strlen(p);char subp[pl+1][pl+1];int next[pl+1];for(int k=0;k&lt;pl;k++){strncpy(subp[k],p,k+1);}//can optimizedfor(int k=0;k&lt;pl;k++){string tl;string tr;int len_sub=k+1;int len=0;for(int m=len_sub-1;m&gt;0;m--){tl=string(subp[k],subp[k]+m);tr=string(subp[k]+len_sub-m,subp[k]+len_sub);if(tl==tr){len=m;break;}}next[k]=len;}for(int k=pl-1;k&gt;0;k--){next[k]=next[k-1];}next[0]=-1;//matchint i=0;int j=0;while(1){if(s[i]==p[j]){i++;j++;}else{int t=next[j];if(t==-1){i++;j=0;}else{j=t;}}if(j&gt;=pl){return i-pl;}if(i&gt;=sl){return -1;}}}int main(){freopen(&quot;input.txt&quot;,&quot;r&quot;,stdin);char a[100];char b[100];cin&gt;&gt;a&gt;&gt;b;cout&lt;&lt;kmp(a,b);return 0;} bmç®—æ³• //bm algorithm three to five times faster than kmp in genneral#include&lt;iostream&gt;#include&lt;algorithm&gt;#include&lt;cstring&gt;#include&lt;string&gt;#include&lt;vector&gt;using namespace std;int get_badc(char t,char a[]){int k=strlen(a)-1;while(1){if(k&gt;=0) //assert{if(a[k--]==t){return k+1;}}else return -1;}}int get_goods(vector&lt;char&gt; t,char a[]) //most difficult{int k=strlen(a)-1;vector&lt;char&gt; temp;int max_len=0;int m=0;int loc=-1;while(1){ if(k&gt;=0) //assert{if(t[m]==a[k]){temp.push_back(a[k]);m++;k--;}else{m=0;k--;if(temp.size()&gt;max_len){max_len=temp.size();loc=k+1;temp.clear();}}}else return loc;}}int bm(char s[],char p[]){int sl=strlen(s);int pl=strlen(p);int i=pl-1;int j=pl-1;while(1){if(i&lt;sl and j&gt;=0) //asseert // i&lt;sl !!!!!!!!!! not i&gt;0{if(s[i]==p[j]){vector&lt;char&gt; goods;goods.push_back(s[i]);while(1){i--;j--;if(i&gt;=0 and j&gt;=0) //assert{if(s[i]!=p[j]){char badc1=s[i];string str(p,p+pl-goods.size());int loc1=get_badc(badc1,&amp;str[0]);int loc2=get_goods(goods,&amp;str[0]);if(loc1==-1 and loc2!=-1){i+=pl-1-loc2;j=pl-1;}else if(loc2==-1 and loc1!=-1){i+=pl-1-loc1;j=pl-1;}else{i+=pl-1-min(loc1,loc2);j=pl-1;}}else{goods.push_back(s[i]); //still add in good-suffix}}else break;}}else //only have bad-character{char badc2=s[i];int loc=get_badc(badc2,p);i+=pl-1-loc;j=pl-1;}}else if(i&gt;=sl) return -1; //!!!!!!!!!!!!!else return i+1; //!!!!!!!!!!!!}}int main(){freopen(&quot;input.txt&quot;,&quot;r&quot;,stdin);char a[100];char b[100];cin&gt;&gt;a&gt;&gt;b;cout&lt;&lt;b&lt;&lt;endl&lt;&lt;a;cout&lt;&lt;bm(b,a)&lt;&lt;endl;return 0;}

